      rdfs:label "price specification under 18 "@en , "costo per i ragazzi sotto i 18 anni "@it ;
      cis:hasCurrency cis:Euro ;
      cis:hasCurrencyValue
              "5"^^xsd:float .


offer:Biblioteca_Nazionale_Centrale_base_offer
